package week1.day2;

public class NegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int givenNumber = -40;
		int positiveNumber;
		if (givenNumber <0) {
			positiveNumber= givenNumber * -1;
			System.out.println("The Number" +givenNumber + " "+ "is converted to "+ positiveNumber);
		}

	}

}
