package week1.day2;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int input =35;
		if (input < 0 ) {
			System.out.println(input + " "+"is a Negative Number");
		}
			else if (input > 0)
			{
				System.out.println(input + " "+ "is a Postive Number");
			}
		else {
			System.out.println(input + " " + "is Neither a Postive Nor a Negative Number");
		}
		}

	}


